INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (1, '中美友好通商航海条约', 1, 0, '两人尾下有月牙一，内画玉兔，周画圆圈以线相连象征月。画面四周遍布大小相等的圆圈，以线相连象征星辰。', 0, 0, 1, '2024-07-12 16:39:15', '1938-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (2, '马夫服', 2, 1, '正在牵马的马夫著红色大褶衣。可见隋代大褶衣不是士人阶层所专有，劳动者中也可以穿著。', 0, 0, 1, '2024-07-12 16:39:15', '1938-10-14 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (3, '油画作品—千年乐舞', 1, 1, '创作以敦煌莫高窟唐代第220窟北壁壁画为蓝本，以数码手绘方式重绘了壁画中标志性的盛大乐队和燃灯菩萨场景。基于对原壁画的色彩研究，本画面尝试再现壁画千年前的鲜亮色彩，既是对敦煌美术的致敬，也是对数字化重绘壁画的探索。', 0, 0, 1, '2024-07-12 16:39:15', '1948-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (4, '自然神及偶像崇拜', 1, 1, '自然神及偶像崇拜：古印度月神。月神身着菩萨装，双手合十坐于月轮中。车两个各有两鹅，相背飞翔，表示月亮运转。', 0, 0, 1, '2024-07-12 16:39:15', '1938-12-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (5, '九色鹿', 4, 3, '这份专利文件主要包括专利申请书、专利证书以及其他相关文件。该文件详细记录了发明创造的内容、实施方式、技术特点以及专利权范围等信息。其中涉及的发明创造涵盖了工业、农业、医疗等各个领域，例如机械制造、纺织、化工、医学等。这份专利文件反映了当时国内科技创新的成果和水平，对于了解和研究民国时期的科学技术发展具有重要的价值。', 0, 0, 1, '2024-07-12 16:39:15', '1935-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (6, '五十一面观音变之酿酒', 5, 2, '画面中央画一灶台，上安一套层叠覆压的方形器皿。科技史学家对此详加考证，认为该器皿应是用于蒸馏的蒸馏器。 灶台边有两名妇女，一人蹲在灶前填薪，手中拿着吹火筒，炉膛内火焰炽烈，灶台顶部的烟囱冒出滚滚浓烟；一人右手端着碗，正在与填薪者交谈，像是品尝过后评说新酒的优劣。地上放着木水桶、酒壶、高足碗、贮酒槽等。', 0, 0, 1, '2024-07-12 16:39:15', '1935-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (7, '天乐遐想', 6, 2, '画面灵感源自敦煌莫高窟唐代第220窟壁画中的“不鼓自鸣”图像，取其中代表性的乐器进行重绘，包含羯鼓、花边阮、方响、箜篌、鸡娄鼓、笛等。画面由“不鼓自鸣”所代表的佛国天乐展开想象，结合孔明灯意向，表达“天乐”向人间播撒的美好情景。', 0, 0, 1, '2024-07-12 16:39:15', '1935-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (8, '云样透花图案的香炉', 7, 3, '香炉有双层莲花底托，下有四叶形、四脚柱的底座。香炉有盖，其下部呈尖角的扇贝形，沿底圆顶向下弯曲，圆顶有双层云样透花图案。从香炉两侧伸出的莲枝和雅致的莲叶托一小花瓶。花瓶装饰有和香炉顶一样的图案。此图案与西安附近何家村发现的8世纪初的随葬品银香炉很相似。香炉两侧为莲座宝瓶。', 0, 0, 1, '2024-07-12 16:39:15', '1935-07-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (9, '胡旋舞五', 2, 0, '修改一下内容： 二舞伎作“吸腿”姿立于小圆毯上，双手一上一下对称挥巾作舞。二舞伎舞姿完全相同，只是左右各异，这是双人舞常用的编排手法。', 0, 0, 1, '2024-07-12 16:39:15', '1935-08-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (10, '拽赶卧驴', 7, 3, '拽赶卧驴：一头不堪重负的毛驴因困乏而卧倒在途中，前面一人在用力拉，后面一人执鞭猛抽驴之臀部。这幅画生动的表现了普通百姓的生活场景，同时也在一定程度上反映了登五台山“圣城”的艰辛。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (11, '小说手稿', 5, 3, '该小说手稿讲述了一个发生在民国时期的故事，地点主要设定在繁华的上海和战火纷飞的东北。故事背景涉及当时的政治、经济、文化等多个方面，展现了民国时期的社会风貌和人民的生活状态。该手稿的主题是探讨人性、爱情、家族荣誉等，情节曲折复杂，引人入胜。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (12, '佛传故事之乘象入胎', 3, 3, '菩萨头戴宝冠，璎珞帔巾绕身，上着抹胸覆膊衣，下着彩色百褶长裙。从菩萨的帔巾到衣裙，都应源自贵妇华贵的盛装。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (13, '艺术绘画—徐悲鸿画作', 4, 3, '作品完全以牛为表现对象，无背景衬托，造型准确生动，设色清淡古朴，浓淡渲染有别，画面层次丰富，达到了形神兼备之境界。以牛入画是中国古代绘画的传统题材之一，体现了农业古国以农为本的主导思想。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (14, '经变画乐伎', 3, 3, '这是大型经变乐队中弹箜篌乐伎特写。乐伎踞坐，怀抱大箜篌置于胸前，双手拨弦。\r\n\r\n', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (15, '孔雀、鹦鹉与双首雁', 2, 3, '孔雀、鹦鹉与双首雁：在观无量寿经变的佛国舞乐图下方，祥禽也随乐起舞，鹦鹉鼓动着翅膀，回首望着乐队；孔雀高举莲瓣形尾羽，轻踏舞步；而一只奇异的双首雁望着孔雀。双首雁可能是佛经中所说的共命鸟，象征同生死，共命运。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (16, '忍冬鹦鹉火焰纹禅室', 3, 3, ' 忍冬鹦鹉火焰纹禅室楣饰：禅室楣饰亦如佛龛，绘以缠枝忍冬纹，缠枝婉转回旋，叶色青、绿、朱、紫相间，叶中画两双绿羽红嘴鹦鹉，相对而立，回头张望。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (17, '普贤菩萨赴会图', 8, 3, '《法华经・普贤菩萨劝发品》说，普贤菩萨于实威德王佛国，遥闻娑婆世界耆闍崛山，请释迦说法。图为普贤菩萨乘象偕诸大菩萨、诸天龙等各显神通地前来听法。普贤菩萨与文殊师利是释迦的左右胁侍，经常同时绘出。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (18, '佛传故事', 6, 3, '北壁西起第二扇（第二十六）1.夜叉开城门无声。2.诸天引导太子而行。3.太子至弥尼迦跋伽婆仙人处歇息4.太子解宝冠交车匿还父王。5.太子持宝刀割发。6.净居天化作净发师为太子净发。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (19, '西方雕塑—和平女神像', 5, 2, '和平女神像：欧战纪念碑，又名和平女神像，民国时期坐落于今延安东路外滩，面朝西，为纪念在第一次世界大战期间上海外国侨民回国参战的死难者而建。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (20, '大红袈裟释迦说法图', 4, 3, '释迦佛交脚而坐，身着大红田相袈裟，作说法相，头光和背光使佛像更稳健庄重。两侧的菩萨，仍为北朝时的裙披式服装。菩萨面部晕染采用浓重的西域式晕染法，仍保留着早期人物画的风格特征。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (21, '坚守大漠 甘于奉献', 1, 1, '20世纪40年代，常书鸿、段文杰、欧阳琳、孙儒僩、史苇湘等一批前辈专家，满怀对敦煌艺术的向往来到莫高窟。伴着大漠戈壁，土屋油灯，在这里扎下根来，开创了敦煌石窟保护、临摹和研究的基业。 中华人民共和国成立后，又有李其琼、李贞伯、万庚育、李云鹤、关友惠、刘玉权、贺世哲、施萍婷、李永宁、孙修身、樊锦诗等一批专家学者响应祖国号召，从四面八方来到莫高窟。与常书鸿、段文杰等先生一样，他们来到莫高窟后就再也没有离开过。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (22, '韩滉五牛图卷', 1, 0, '《五牛图》卷，唐，韩滉作，纸本，设色，纵20.8厘米，横139.8厘米。\r\n　　本图无作者款印，本幅及尾纸上有赵孟頫、孙弘、项元汴、弘历、金农等十四家题记。\r\n作品完全以牛为表现对象，无背景衬托，造型准确生动，设色清淡古朴，浓淡渲染有别，画面层次丰富，达到了形神兼备之境界。以牛入画是中国古代绘画的传统题材之一，体现了农业古国以农为本的主导思想。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (23, '菩萨坐像残片', 3, 0, '　菩萨坐像残片，唐，纸本，设色，纵5.5厘米，横22.5厘米。敦煌遗画。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (24, '伏羲女娲像页', 4, 2, '两人尾下有月牙一，内画玉兔，周画圆圈以线相连象征月。画面四周遍布大小相等的圆圈，以线相连象征星辰。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (25, '展子虔游春图卷', 5, 3, '《游春图》卷，隋，展子虔作，绢本，设色，纵43厘米，横80.5厘米。彩为主调，建筑物和人物、马匹间以红、白诸色，既统一和谐，又富有变化。亮丽的色彩亦更好地衬托出大好河山盎然勃发的春天气息。\r\n　　《游春图》卷历来被认为是隋朝画家展子虔的作品，代表了中国早期山水画的面貌。近些年有学者对于这幅作品的作者和时代提出了不同的看法，引起了讨论。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (26, '顾恺之列女仁智图卷（宋摹）', 7, 2, '汉成帝沉湎于酒色，宠信赵飞燕姐妹，朝政大权旁落于外戚手中，危及刘氏政权。楚元王四世孙光禄大夫刘向（前77－前6年）针对这一情况，采摘自古以来诗书上所记载的贤妃、贞妇、宠姬等资料，编辑成《列女传》一书呈送汉成帝，希望他从中吸取经验教训，以维护刘氏政权。其人物线条粗犷流畅，造型准确。特别是对妇女的描绘，体态轻盈，婀娜多姿，尤为绝妙。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
INSERT INTO `archive` (`archiveId`, `archiveName`, `institutionId`, `archiveType`, `archiveContent`, `views`, `archiveStateType`, `creatorId`, `creationTime`, `firsttime`) VALUES (27, '乾隆帝元宵行乐图轴', 5, 1, '《乾隆帝元宵行乐图》轴，清乾隆，绢本，设色，纵302厘米，横204.3厘米。　　此图描绘的是清代乾隆皇帝与皇族子弟们在宫苑内庆贺元宵节的情景。元宵节，又称为“上元节”、“小正月”、“元夕”或“灯节”，是岁朝之后的第一个重要节日。', 0, 0, 1, '2024-07-12 16:39:15', '1940-10-11 00:49:24');
