mapper.select:
SELECT 	c.*,
        u.userName AS creator FROM component c
                  LEFT JOIN user u ON c.creatorId = u.userId


        AND c.componentName LIKE CONCAT('%', #{componentName}, '%')


        AND c.componentType = #{componentType}


        AND u.userName LIKE CONCAT('%', #{userName}, '%')


        AND c.state = #{state}


ORDER BY ${orderColumn} ${isAsc ? 'ASC' : 'DESC'}
