mapper.select:
SELECT f.*,u.userName AS creator FROM fiber f LEFT JOIN user u ON f.creatorId = u.userId
AND f.fiberType LIKE CONCAT('%', #{fiberType}, '%')
AND f.characteristic LIKE CONCAT('%', #{characteristic}, '%')
AND u.userName LIKE CONCAT('%', #{userName}, '%')
AND f.state = #{state}
ORDER BY ${orderColumn} ${isAsc ? 'ASC' : 'DESC'}
