mapper.select:
SELECT p.*,u.userName AS creator FROM permission p LEFT JOIN user u ON p.creatorId = u.userId
AND p.permissionName LIKE CONCAT('%', #{permissionName}, '%')
AND p.description = LIKE CONCAT('%', #{description}, '%')
AND u.userName LIKE CONCAT('%', #{userName}, '%')
AND p.state = #{state}
ORDER BY ${orderColumn} ${isAsc ? 'ASC' : 'DESC'}
