mapper.select:
SELECT r.*,u.userName AS creator FROM role r LEFT JOIN user u ON r.creatorId = u.userId
AND r.roleName LIKE CONCAT('%', #{roleName}, '%')
AND r.description = LIKE CONCAT('%', #{description}, '%')
AND u.userName LIKE CONCAT('%', #{userName}, '%')
AND r.state = #{state}
ORDER BY ${orderColumn} ${isAsc ? 'ASC' : 'DESC'}
