mapper.select:
SELECT 	w.*,wt.typeName,u.userName AS creator FROM word w
                  LEFT JOIN wordtype wt ON w.type = wt.wordTypeId
                                            LEFT JOIN user u ON w.creatorId = u.userId


        AND w.wordName LIKE CONCAT('%', #{wordName}, '%')


        AND w.type = #{type}


        AND u.userName LIKE CONCAT('%', #{userName}, '%')


        AND w.state = #{state}


ORDER BY ${orderColumn} ${isAsc ? 'ASC' : 'DESC'}
