mapper.select:
SELECT w.*,u.userName FROM wordType w LEFT JOIN user u ON w.creatorId = u.userId
AND w.typeName LIKE CONCAT('%', #{typeName}, '%')
AND w.description = LIKE CONCAT('%', #{description}, '%')
AND u.userName LIKE CONCAT('%', #{userName}, '%')
AND w.state = #{state}
ORDER BY ${orderColumn} ${isAsc ? 'ASC' : 'DESC'}
