mapper.select:
SELECT ap.*, a.archiveName, p.*, u.userName AS creatorName FROM archiveComponent ap LEFT JOIN archive a ON ap.archiveId = a.archiveId LEFT JOIN component p ON ap.componentId = p.componentId LEFT JOIN user u ON ap.creatorId = u.userId
AND ap.archiveId = #{id}
AND ap.status = #{type}

SELECT ap.*, a.archiveName, p.componentName, u.userName AS creatorName FROM archiveComponent ap LEFT JOIN archive a ON ap.archiveId = a.archiveId LEFT JOIN component p ON ap.componentId = p.componentId LEFT JOIN user u ON ap.creatorId = u.userId
AND ap.componentId = #{id}
AND ap.status = #{type}
